if ( ! defined( 'WPNUX_API_URL' ) ) {

	define( 'WPNUX_API_URL', 'https://wpnux.godaddy.com/v2/api' );

}

if ( ! defined( 'WPEM_LOG_FILE' ) ) {

	define( 'WPEM_LOG_FILE', WP_CONTENT_DIR . '/wpem.log' );

}

add_action( 'init', 'wpem_init' );
add_action( 'wp_ajax_wpem_nux_import', 'wpem_nux_import_ajax_callback' );
add_action( 'wp_ajax_wpem_nux_optout', 'wpem_nux_optout_ajax_callback' );

/**
 * Allow external HTTP requests to our API.
 */
add_filter( 'http_request_host_is_external', function( $allow, $host ) {

	return wp_parse_url( WPNUX_API_URL, PHP_URL_HOST ) === $host ? true : $allow;

}, 10, 2 );

/**
 * Initialize the plugin.
 *
 * @action init
 */
function wpem_init() {

	if ( defined( 'DOING_AJAX' ) && DOING_AJAX ) {

		return;

	}

	if ( ! wpem_is_fresh_site() ) {

		wpem_self_destruct();

		return;

	}

	if ( ! is_admin() || ! current_user_can( 'install_plugins' ) || filter_input( INPUT_GET, 'bypass_wpem' ) ) {

		return;

	}

	if ( 'wpem' !== filter_input( INPUT_GET, 'page' ) ) {

		wp_safe_redirect( add_query_arg( 'page', 'wpem', admin_url() ) );

		exit;

	}

	// Ensure the API is reachable before rendering the page.
	if ( ! wpnux_api_health_check() ) {

		wpem_self_destruct();

		wp_safe_redirect( admin_url() );

		exit;

	}

	add_action( 'admin_menu', function () {

		add_dashboard_page(
			'WP Easy Mode', // Translation not needed.
			'Easy Mode', // Translation not needed.
			'install_plugins',
			'wpem',
			'wpem_render_page'
		);

	} );

	add_action( 'admin_init', 'wpem_render_page' );

}

/**
 * Check whether the WPNUX API is reachable.
 *
 * @return bool
 */
function wpnux_api_health_check() {

	wpem_log( sprintf( '%s - start', __FUNCTION__ ) );

	// Bail when debugging.
	if ( WP_DEBUG ) {

		wpem_log( sprintf( '%s - skipped', __FUNCTION__ ) );

		return true;

	}

	$response = wp_remote_get( WPNUX_API_URL, [
		'headers' => [
			'Content-Type' => 'application/json',
		],
		'sslverify' => true,
		'timeout'   => 10,
	] );

	if ( is_wp_error( $response ) ) {

		wpem_log( sprintf( '%s - %s', __FUNCTION__, $response->get_error_message() ) );

		return false;

	}

	$response_code = wp_remote_retrieve_response_code( $response );

	if ( 200 !== $response_code ) {

		wpem_log( sprintf( '%s - %s:%s', __FUNCTION__, $response_code, wp_remote_retrieve_response_message( $response ) ) );

		return false;

	}

	$body = json_decode( wp_remote_retrieve_body( $response ), true );

	if ( empty( $body['success'] ) ) {

		wpem_log( sprintf( '%s - The service has been temporarily disabled', __FUNCTION__ ) );

		return false;

	}

	wpem_log( sprintf( '%s - success', __FUNCTION__ ) );

	return true;

}

/**
 * Append a line to log file for debugging.
 *
 * @param string $line
 */
function wpem_log( $line ) {

	global $wpem_log;

	if ( ! $wpem_log ) {

		$wpem_log = fopen( WPEM_LOG_FILE, 'a' );

	}

	fwrite( $wpem_log, sprintf( '%s %s', current_time( 'mysql', 1 ), $line ) . PHP_EOL );

}

/**
 * Close file pointer resource for log file.
 */
function wpem_log_close() {

	global $wpem_log;

	if ( $wpem_log ) {

		fclose( $wpem_log );

	}

	$debug_log = @file_get_contents( WPEM_LOG_FILE );

	if ( $debug_log ) {

		update_option( 'wpnux_debug_log', $debug_log, false );

	}

	@unlink( WPEM_LOG_FILE );

}

/**
 * Check whether this site is fresh (has no post content or users).
 */
function wpem_is_fresh_site() {

	global $wpdb;

	$posts = (int) $wpdb->get_var( "SELECT COUNT(ID) FROM `{$wpdb->posts}` WHERE post_status != 'auto-draft';" );
	$users = (int) $wpdb->get_var( "SELECT COUNT(ID) FROM `{$wpdb->users}`;" );
	$wpnux = (int) $wpdb->get_var( "SELECT COUNT(option_id) FROM `{$wpdb->options}` WHERE option_name = 'wpnux_imported';" );

	return ( $posts <= 3 && 1 === $users && ! $wpnux );

}

/**
 * When called, this PHP file will delete itself, unless:
 *
 * 1. The site is in debug mode (WP_DEBUG = true)
 * 2. The parent directory of this file is under source control (git)
 */
function wpem_self_destruct() {

	if ( ! WP_DEBUG && ! file_exists( __DIR__ . '/.git' ) ) {

		if ( ! class_exists( 'WP_Filesystem' ) ) {

			require_once ABSPATH . 'wp-admin/includes/file.php';

		}

		WP_Filesystem();

		global $wp_filesystem;

		$wp_filesystem->delete( __FILE__ );

		wpem_log( __FUNCTION__ );

	}

	if ( ! get_option( 'wpnux_imported' ) ) {

		update_option( 'wpnux_imported', 0, false );

	}

	wpem_log_close();

}

function wpem_localize( $string ) {

	$strings = json_decode( $GLOBALS['wpem_localize_strings'], true );

	return isset( $strings[ $string ][ get_locale() ] ) ? $strings[ $string ][ get_locale() ] : $string;

}

function wpem_wpnux_api_url( $path, $args = [] ) {

	return add_query_arg( $args, trailingslashit( WPNUX_API_URL ) . $path );

}

function wpem_nux_import_ajax_callback() {

	wpem_log( sprintf( '%s - start', __FUNCTION__ ) );

	$nonce = filter_input( INPUT_POST, 'nonce', FILTER_SANITIZE_STRING );

	if ( 1 !== wp_verify_nonce( $nonce, 'wpem_nux_import' ) ) {

		wpem_log( sprintf( '%s - invalid_nonce:%s', __FUNCTION__, $nonce ) );

		wp_send_json_error( 'Invalid nonce', 403 );

	}

	$features    = explode( ',', filter_input( INPUT_POST, 'features', FILTER_SANITIZE_STRING ) );
	$preview_url = filter_input( INPUT_POST, 'preview_url', FILTER_SANITIZE_STRING );
	$persona     = filter_input( INPUT_POST, 'persona', FILTER_SANITIZE_STRING );
	$vertical    = filter_input( INPUT_POST, 'vertical', FILTER_SANITIZE_STRING );

	$result = wpem_nux_import( $preview_url, $features, $persona, $vertical );

	if ( true !== $result ) {

		wpem_log( sprintf( '%s - import_error:%s', __FUNCTION__, $result ) );

		wpem_self_destruct();

		wp_send_json_error( "Import error: {$result}", 500 );

	}

	wpem_log( sprintf( '%s - success', __FUNCTION__ ) );

	wpem_self_destruct();

	wp_send_json_success();

}

function wpem_nux_optout_ajax_callback() {

	wpem_log( sprintf( '%s - start', __FUNCTION__ ) );

	$nonce = filter_input( INPUT_POST, 'nonce', FILTER_SANITIZE_STRING );

	if ( 1 !== wp_verify_nonce( $nonce, 'wpem_nux_optout' ) ) {

		wpem_log( sprintf( '%s - invalid_nonce:%s', __FUNCTION__, $nonce ) );

		wpem_self_destruct();

		wp_send_json_error( 'Invalid nonce', 403 );

	}

	// Log the optout.
	wp_remote_post( esc_url_raw( wpem_wpnux_api_url( 'optout' ) ), [
		'body' => wp_json_encode( [
			'domain'       => wp_parse_url( home_url(), PHP_URL_HOST ),
			'hostname'     => gethostname(),
			'locale'       => get_locale(),
			'country'      => wpem_get_geodata( 'country_code' ),
			'persona'      => filter_input( INPUT_POST, 'persona', FILTER_SANITIZE_STRING ),
			'reasons'      => explode( ',', filter_input( INPUT_POST, 'reasons', FILTER_SANITIZE_STRING ) ),
			'reason_other' => filter_input( INPUT_POST, 'reason_other', FILTER_SANITIZE_STRING ),
		] ),
		'headers' => [
			'Content-Type' => 'application/json',
		],
		'sslverify' => ! WP_DEBUG,
		'blocking'  => false, // Fire and forget.
		'timeout'   => 5,
	] );

	wpem_log( sprintf( '%s - success', __FUNCTION__ ) );

	wpem_self_destruct();

	wp_send_json_success();

}

function wpem_nux_import( $preview_url, $features, $persona = null, $vertical = null ) {

	$start = microtime( true );

	$domain = wp_parse_url( home_url(), PHP_URL_HOST );

	$args = [
		'action'   => 'export',
		'country'  => wpem_get_geodata( 'country_code' ),
		'domain'   => $domain,
		'hostname' => gethostname(),
		'features' => $features,
		'locale'   => get_locale(),
		'log'      => 1, // Needed to save an `export_uid` to the WPNUX DB.
		'persona'  => $persona,
		'pl'       => 1, // Always force private label mode.
		'premium'  => 0, // Never allow premium features.
		'vertical' => $vertical,
	];

	$url = esc_url_raw( add_query_arg( $args, $preview_url ) );

	$response = wp_remote_get( $url, [
		'headers' => [
			'Content-Type' => 'application/json',
		],
		'sslverify' => ! WP_DEBUG,
		'timeout'   => 15,
	] );

	if ( is_wp_error( $response ) ) {

		return sprintf( '%s:%s', $response->get_error_code(), $response->get_error_message() );

	}

	if ( 200 !== wp_remote_retrieve_response_code( $response ) ) {

		return 'API response code ' . wp_remote_retrieve_response_code( $response );

	}

	$data = json_decode( wp_remote_retrieve_body( $response ), true );

	if ( false === $data || empty( $data['content'] ) || ! is_array( $data['content'] ) ) {

		return 'Missing starter content';

	}

	if ( empty( $data['_meta']['export_uid'] ) || ! wp_is_uuid( $data['_meta']['export_uid'] ) ) {

		return 'Missing valid export UID';

	}

	if ( ! class_exists( 'WP_Filesystem' ) ) {

		require_once ABSPATH . 'wp-admin/includes/file.php';

	}

	WP_Filesystem();

	// Themes.
	if ( ! empty( $data['themes'] ) && is_array( $data['themes'] ) ) {

		$activate = ! empty( $data['content']['options']['stylesheet'] ) ? $data['content']['options']['stylesheet'] : ( ! empty( $data['content']['options']['template'] ) ? $data['content']['options']['template'] : null );

		$result = wpem_install_extensions( 'theme', $data['themes'], array_filter( [ $activate ] ) );

		if ( is_wp_error( $result ) ) {

			return sprintf( '%s:%s', $result->get_error_code(), $result->get_error_message() );

		}

	}

	// Plugins.
	if ( ! empty( $data['plugins'] ) && is_array( $data['plugins'] ) ) {

		$result = wpem_install_extensions( 'plugin', $data['plugins'] );

		if ( is_wp_error( $result ) ) {

			return sprintf( '%s:%s', $result->get_error_code(), $result->get_error_message() );

		}

	}

	wpem_delete_existing_content();

	$wpnux_importer = new WPNUX_Importer( $data['content'] );
	$wpnux_importer->run();

	$export_uid = $data['_meta']['export_uid'];
	$took_ms    = round( ( microtime( true ) - $start ) * 1000 );

	update_option( 'wpnux_export_uid', $export_uid, false );
	update_option( 'wpnux_export_data', wp_json_encode( $data ), false );
	update_option( 'wpnux_imported', current_time( 'mysql', 1 ), false );
	update_option( 'wpnux_took_ms', $took_ms, false );

	// Tell the API the import completed and how long it took in milliseconds.
	wp_remote_post( esc_url_raw( wpem_wpnux_api_url( "claim/{$export_uid}" ) ), [
		'body' => wp_json_encode( [
			'domain'  => $domain,
			'took_ms' => $took_ms,
		] ),
		'headers' => [
			'Content-Type' => 'application/json',
		],
		'sslverify' => ! WP_DEBUG,
		'blocking'  => false, // Fire and forget.
		'timeout'   => 5,
	] );

	return true;

}

function wpem_delete_existing_content() {

	global $wpdb;

	// Options.
	$wpdb->query( "DELETE FROM `{$wpdb->options}` WHERE ( `option_name` LIKE 'wpnux_%' ) OR ( `option_name` LIKE '%_transient_%' ) OR ( `option_name` LIKE 'theme_mods_%' ) OR ( `option_name` LIKE 'widget_%' );" );

	// Widgets.
	update_option( 'sidebars_widgets', [ 'wp_inactive_widgets' => [] ] );

	// Keep unpublished drafts.
	$keep_post_ids = array_map( 'absint', $wpdb->get_col( "SELECT ID FROM `{$wpdb->posts}` WHERE `post_type` IN('page', 'post') AND `post_status` = 'draft';" ) );

	// Posts and post meta.
	if ( $keep_post_ids ) {

		$wpdb->query( sprintf( "DELETE p, pm FROM `{$wpdb->posts}` p LEFT JOIN `{$wpdb->postmeta}` pm ON pm.`post_id` = p.`ID` WHERE p.`ID` NOT IN(%s);", implode( ', ', $keep_post_ids ) ) );

	} else {

		$wpdb->query( "TRUNCATE TABLE `{$wpdb->posts}`" );
		$wpdb->query( "TRUNCATE TABLE `{$wpdb->postmeta}`" );

	}

	// Content.
	$wpdb->query( "TRUNCATE TABLE `{$wpdb->comments}`" );
	$wpdb->query( "TRUNCATE TABLE `{$wpdb->commentmeta}`" );
	$wpdb->query( "TRUNCATE TABLE `{$wpdb->links}`" );
	$wpdb->query( "TRUNCATE TABLE `{$wpdb->terms}`" );
	$wpdb->query( "TRUNCATE TABLE `{$wpdb->term_taxonomy}`" );
	$wpdb->query( "TRUNCATE TABLE `{$wpdb->term_relationships}`" );
	$wpdb->query( "TRUNCATE TABLE `{$wpdb->termmeta}`" );

	// Uploads.
	$uploads = wp_upload_dir( null, false, true );

	if ( ! empty( $uploads['basedir'] ) ) {

		exec( sprintf( 'rm -rf %s/*', untrailingslashit( $uploads['basedir'] ) ) );

	}

	// Flush cache.
	wp_cache_flush();
	wp_cache_delete( 'alloptions', 'options' );

}

function wpem_install_extensions( $type, $array, $activate = [] ) {

	$custom_urls   = preg_grep( '~^https?://~', $array );
	$wp_org_slugs  = array_diff( $array, $custom_urls );
	$download_urls = $custom_urls;

	foreach ( $wp_org_slugs as $slug ) {

		$download_urls[] = "https://downloads.wordpress.org/{$type}/{$slug}.zip";

	}

	$destpath = ( 'theme' === $type ) ? get_theme_root() : WP_PLUGIN_DIR;

	foreach ( $download_urls as $url ) {

		$filepath = download_url( $url );
		$unzipped = unzip_file( $filepath, $destpath );

		@unlink( $filepath );

		if ( is_wp_error( $unzipped ) ) {

			return $unzipped;

		}

	}

	if ( 'theme' === $type ) {

		$activate = $activate ? $activate : ( $custom_urls ? array_keys( $custom_urls ) : ( $wp_org_slugs ? $wp_org_slugs : [] ) );
		$activate = end( $activate );

		if ( $activate ) {

			switch_theme( $activate );

			wpem_log( sprintf( '%s:switch_theme - %s', __FUNCTION__, $activate ) );

		}

	} else {

		$activate = $activate ? $activate : array_keys( $array );

		$result = activate_plugins( $activate );

		if ( is_wp_error( $result ) ) {

			return $result;

		}

		wpem_log( sprintf( '%s:activate_plugins - %s', __FUNCTION__, wp_json_encode( $activate ) ) );

	}

	return true;

}

/**
 * Round a float value and preserve trailing zeros.
 *
 * @return float
 */
function wpem_round( $value, $precision = 3 ) {

	$precision = absint( $precision );

	return sprintf( "%.{$precision}f", round( $value, $precision ) );

}

function wpem_get_geodata( $key = null ) {

	global $wpem_geodata;

	$wpem_geodata = is_a( $wpem_geodata, 'WPEM_Geodata' ) ? $wpem_geodata : new WPEM_Geodata;

	$key = $key ? $key : 'data';

	return $wpem_geodata->{$key};

}

final class WPEM_Geodata {

	const API_URL     = 'https://ipapi.co/%s/json';
	const ALT_API_URL = 'http://ip-api.com/json/%s';

	private $data    = [];
	private $timeout = 5;

	public function __construct() {

		$ip = filter_input( INPUT_SERVER, 'REMOTE_ADDR', FILTER_VALIDATE_IP );

		if ( $this->is_public_ip( $ip ) ) {

			$this->data = $this->get_geodata( $ip );

		}

	}

	public function __get( $key ) {

		if ( property_exists( $this, $key ) ) {

			return $this->{$key};

		}

		if ( isset( $this->data[ $key ] ) ) {

			return $this->data[ $key ];

		}

		return null;

	}

	/**
	 * Check if an IP address is valid and public.
	 *
	 * @param string $ip
	 *
	 * @return bool
	 */
	private function is_public_ip( $ip ) {

		// IPv4.
		$ip = filter_var( $ip, FILTER_VALIDATE_IP, FILTER_FLAG_IPV4 | FILTER_FLAG_NO_PRIV_RANGE | FILTER_FLAG_NO_RES_RANGE );

		if ( $ip ) {

			return true;

		}

		// IPv6.
		$ip = filter_var( $ip, FILTER_VALIDATE_IP, FILTER_FLAG_IPV6 );

		if ( $ip ) {

			return true;

		}

		return false;

	}

	/**
	 * Normalize geodata between multiple API sources.
	 *
	 * @param array $geodata
	 *
	 * @return array
	 */
	private function normalize( $geodata ) {

		$mappings = [
			// ipapi.co
			'country'     => 'country_code',
			'postal'      => 'postal_code',
			// ip-api.com
			'countryCode' => 'country_code',
			'lat'         => 'latitude',
			'lon'         => 'longitude',
			'zip'         => 'postal_code',
		];

		foreach ( $mappings as $from => $to ) {

			if ( ! isset( $geodata[ $to ] ) && isset( $geodata[ $from ] ) ) {

				$geodata[ $to ] = $geodata[ $from ];

			}

		}

		// Special case for ipapi.co.
		if ( isset( $geodata['region'] ) && isset( $geodata['region_code'] ) ) {

			$geodata['region_name'] = $geodata['region'];

		}

		// Special case for ip-api.com.
		if ( isset( $geodata['region'] ) && isset( $geodata['regionName'] ) ) {

			$geodata['region_code'] = $geodata['region'];
			$geodata['region_name'] = $geodata['regionName'];

		}

		$geodata['latitude']  = wpem_round( $geodata['latitude'] );
		$geodata['longitude'] = wpem_round( $geodata['longitude'] );

		$props = [
			'city',
			'country_code',
			'country_name',
			'currency',
			'ip',
			'latitude',
			'longitude',
			'postal_code',
			'region_code',
			'region_name',
			'timezone',
		];

		foreach ( $props as $prop ) {

			if ( ! array_key_exists( $prop, $geodata ) ) {

				$geodata[ $prop ] = null;

			}

		}

		$geodata = array_intersect_key( $geodata, array_flip( $props ) );

		ksort( $geodata );

		return $geodata;

	}

	/**
	 * Return the geodata of a given IP address.
	 *
	 * @param  string $ip
	 *
	 * @return array
	 */
	private function get_geodata( $ip ) {

		$response = $this->request( self::API_URL, $ip );

		if ( ! $response ) {

			$response = $this->request( self::ALT_API_URL, $ip );

		}

		if ( ! $response ) {

			return [];

		}

		$response['ip'] = $ip; // Ensure there is always an IP property.

		return $this->normalize( $response );

	}

	/**
	 * Request geodata from an API.
	 *
	 * @param string $url
	 * @param string $ip
	 *
	 * @return array|bool
	 */
	private function request( $url, $ip ) {

		$url      = esc_url_raw( sprintf( $url, $ip ) );
		$response = wp_remote_get( $url, [ 'timeout' => $this->timeout ] );

		if ( 200 !== wp_remote_retrieve_response_code( $response ) || is_wp_error( $response ) || empty( $response ) ) {

			return false;

		}

		return json_decode( wp_remote_retrieve_body( $response ), true );

	}

}

final class WPNUX_Importer {

	/**
	 * Some options are arrays that should be merged with pre-existing values, should they exist.
	 *
	 * @var array
	 */
	const MERGEABLE_ARRAY_OPTIONS = [ 'auto_update_plugins', 'auto_update_themes' ];

	/**
	 * Array of starter content.
	 *
	 * @var array
	 */
	public $starter_content = [];

	/**
	 * Array of post objects that have been imported.
	 *
	 * @var array
	 */
	public $posts = [];

	/**
	 * Class constructor.
	 *
	 * @param array $starter_content (optional)
	 *
	 * @return void
	 */
	public function __construct( array $starter_content = [] ) {

		$this->starter_content = $starter_content;

	}

	/**
	 * Apply all starter content in the correct order.
	 *
	 * @return void
	 */
	public function run() {

		wpem_log( sprintf( '%s - start', __METHOD__ ) );

		@set_time_limit( 600 );
		@ini_set( 'memory_limit', '1G' );

		if ( ! empty( $this->starter_content['attachments'] ) ) {

			$this->attachments( $this->starter_content['attachments'] );

		}

		if ( ! empty( $this->starter_content['posts'] ) ) {

			$this->posts( $this->starter_content['posts'] );

		}

		if ( ! empty( $this->starter_content['options'] ) ) {

			$this->options( $this->starter_content['options'] );

		}

		if ( ! empty( $this->starter_content['nav_menus'] ) ) {

			$this->nav_menus( $this->starter_content['nav_menus'] );

		}

		if ( ! empty( $this->starter_content['widgets'] ) ) {

			$this->widgets( $this->starter_content['widgets'] );

		}

		if ( ! empty( $this->starter_content['theme_mods'] ) ) {

			$this->theme_mods( $this->starter_content['theme_mods'] );

		}

		flush_rewrite_rules();

		wpem_log( sprintf( '%s - success', __METHOD__ ) );

	}

	/**
	 * Import attachments.
	 *
	 * @param array $attachments
	 */
	public function attachments( $attachments ) {

		wpem_log( sprintf( '%s - start', __METHOD__ ) );

		$uploads = wp_upload_dir( null, true, true );

		wp_mkdir_p( $uploads['path'] ); // Ensure year/month upload directories exist.

		foreach ( $attachments as $symbol => $postarr ) {

			if ( empty( $postarr['file'] ) ) {

				wpem_log( sprintf( '%s:%s - %s', __METHOD__, $symbol, 'no_file_in_postarr' ) );

				continue;

			}

			$post_id = media_sideload_image( $postarr['file'], null, null, 'id' );

			if ( is_wp_error( $post_id ) ) {

				wpem_log( sprintf( '%s:%s - %s:%s', __METHOD__, $symbol, $post_id->get_error_code(), $post_id->get_error_message() ) );

				continue;

			}

			unset( $postarr['file'] );

			$postarr['ID'] = $post_id;

			wp_update_post( $postarr );

			list( $attachment_url ) = wp_get_attachment_image_src( $post_id, 'full' );

			$this->posts[ $symbol ]            = get_post( $post_id );
			$this->posts[ $symbol ]->permalink = get_permalink( $post_id );
			$this->posts[ $symbol ]->file      = $attachment_url;

			update_post_meta( $post_id, 'wpnux_id', $symbol );

			wpem_log( sprintf( '%s:%s - success', __METHOD__, $symbol ) );

		}

		wpem_log( sprintf( '%s - success', __METHOD__ ) );

	}

	/**
	 * Import posts.
	 *
	 * @param array $posts
	 */
	public function posts( $posts ) {

		wpem_log( sprintf( '%s - start', __METHOD__ ) );

		foreach ( $posts as $symbol => $postarr ) {

			$meta_input             = [ 'wpnux_id' => $symbol ];
			$postarr['meta_input']  = isset( $postarr['meta_input'] ) ? array_merge( $postarr['meta_input'], $meta_input ) : $meta_input;
			$postarr['post_title']  = isset( $postarr['post_title'] ) ? $postarr['post_title'] : $symbol; // Fallback to symbol as title.
			$postarr['post_status'] = isset( $postarr['post_status'] ) ? $postarr['post_status'] : 'publish';

			$post_id = wp_insert_post( $postarr, true );

			if ( is_wp_error( $post_id ) ) {

				wpem_log( sprintf( '%s:%s - %s:%s', __METHOD__, $symbol, $post_id->get_error_code(), $post_id->get_error_message() ) );

				continue;

			}

			if ( ! empty( $postarr['thumbnail'] ) ) {

				set_post_thumbnail( $post_id, $this->expand_post_symbols( $postarr['thumbnail'] ) );

			}

			$this->posts[ $symbol ]            = get_post( $post_id );
			$this->posts[ $symbol ]->permalink = get_permalink( $post_id );

			wpem_log( sprintf( '%s:%s - success', __METHOD__, $symbol ) );

		}

		// Loop through all posts again to expand symbols inside post_content.
		foreach ( $this->posts as $post ) {

			if ( ! empty( $post->ID ) && ! empty( $post->post_content ) ) {

				$post->post_content = $this->expand_post_symbols( $post->post_content );

				wp_update_post( (array) $post );

			}

		}

		wpem_log( sprintf( '%s - success', __METHOD__ ) );

	}

	/**
	 * Import options.
	 *
	 * @param array $options
	 */
	public function options( $options ) {

		wpem_log( sprintf( '%s - start', __METHOD__ ) );

		// We already installed and activated plugins.
		unset( $options['active_plugins'] );

		foreach ( $options as $option => $value ) {

			$value = is_array( $value ) ? array_map( [ $this, 'expand_post_symbols' ], $value ) : $this->expand_post_symbols( $value );

			if ( in_array( $option, self::MERGEABLE_ARRAY_OPTIONS, true ) ) {

				$current = (array) get_option( $option, [] );
				$value   = array_merge( $current, array_diff( (array) $value, $current ) );

			}

			update_option( $option, $value );

			wpem_log( sprintf( '%s:%s - success', __METHOD__, $option ) );

		}

		wpem_log( sprintf( '%s - success', __METHOD__ ) );

	}

	/**
	 * Import nav menus.
	 *
	 * @param array $nav_menus
	 */
	public function nav_menus( $nav_menus ) {

		wpem_log( sprintf( '%s - start', __METHOD__ ) );

		$locations = [];

		foreach ( $nav_menus as $location => $nav_menu ) {

			if ( empty( $nav_menu['name'] ) ) {

				wpem_log( sprintf( '%s:%s - no_menu_name', __METHOD__, $location ) );

				continue;

			}

			if ( empty( $nav_menu['items'] ) ) {

				wpem_log( sprintf( '%s:%s - no_menu_items', __METHOD__, $location ) );

				continue;

			}

			$wp_term = wp_get_nav_menu_object( $nav_menu['name'] );
			$menu_id = ! empty( $wp_term->term_id ) ? $wp_term->term_id : wp_create_nav_menu( $nav_menu['name'] );

			if ( is_wp_error( $menu_id ) ) {

				wpem_log( sprintf( '%s:%s - %s:%s', __METHOD__, $location, $menu_id->get_error_code(), $menu_id->get_error_message() ) );

				continue;

			}

			$locations[ $location ] = $menu_id;

			foreach ( $nav_menu['items'] as $item ) {

				$item['status'] = isset( $item['status'] ) ? $item['status'] : 'publish';

				$menu_item_data = [];

				foreach ( $item as $key => $value ) {

					$menu_item_data[ 'menu-item-' . str_replace( '_', '-', $key ) ] = $this->expand_post_symbols( $value );

				}

				wp_update_nav_menu_item( $menu_id, 0, $menu_item_data );

			}

			wpem_log( sprintf( '%s:%s - success', __METHOD__, $location ) );

		}

		set_theme_mod( 'nav_menu_locations', $locations );

		wpem_log( sprintf( '%s - success', __METHOD__ ) );

	}

	/**
	 * Import widgets.
	 *
	 * @param array $widgets
	 */
	public function widgets( $sidebars ) {

		wpem_log( sprintf( '%s - start', __METHOD__ ) );

		foreach ( $sidebars as $sidebar => $widgets ) {

			foreach ( $widgets as $symbol => $widget ) {

				if ( empty( $widget[0] ) ) {

					wpem_log( sprintf( '%s:%s:%s - no_widget_id', __METHOD__, $sidebar, $symbol ) );

					continue;

				}

				$widget_id        = $widget[0];
				$widget_data      = isset( $widget[1] ) ? (array) $widget[1] : [];
				$sidebars_widgets = get_option( 'sidebars_widgets', [] );
				$widget_instances = get_option( "widget_{$widget_id}", [] );
				$numeric_keys     = array_filter( array_keys( $widget_instances ), 'is_int' );
				$next_key         = $numeric_keys ? max( $numeric_keys ) + 1 : 2;

				if ( ! isset( $sidebars_widgets[ $sidebar ] ) ) {

					$sidebars_widgets[ $sidebar ] = [];

				}

				$sidebars_widgets[ $sidebar ][] = "{$widget_id}-{$next_key}";
				$widget_instances[ $next_key ]  = $widget_data;

				update_option( 'sidebars_widgets', $sidebars_widgets );
				update_option( "widget_{$widget_id}", $widget_instances );

				wpem_log( sprintf( '%s:%s:%s:%s - success', __METHOD__, $sidebar, $symbol, $widget_id ) );

			}

			wpem_log( sprintf( '%s:%s - success', __METHOD__, $sidebar ) );

		}

		wpem_log( sprintf( '%s - success', __METHOD__ ) );

	}

	/**
	 * Import theme mods.
	 *
	 * @param array $theme_mods
	 */
	public function theme_mods( $theme_mods ) {

		wpem_log( sprintf( '%s - start', __METHOD__ ) );

		foreach ( $theme_mods as $name => $value ) {

			set_theme_mod( $name, $this->expand_post_symbols( $value ) );

			wpem_log( sprintf( '%s:%s - success', __METHOD__, $name ) );

		}

		wpem_log( sprintf( '%s - success', __METHOD__ ) );

	}

	/**
	 * Expand all post symbols that exist inside a string, or return the value unchanged.
	 *
	 * @param mixed $value
	 *
	 * @return mixed
	 */
	public function expand_post_symbols( $value ) {

		if ( ! is_string( $value ) || ! preg_match_all( '/{{([\w\-]+):?(\w+)?}}/', $value, $matches ) ) {

			return $value;

		}

		foreach ( $matches[0] as $key => $match ) {

			$symbol   = sanitize_title( $matches[1][ $key ] );
			$property = ! empty( $matches[2][ $key ] ) ? $matches[2][ $key ] : 'ID';
			$expanded = isset( $this->posts[ $symbol ]->$property ) ? $this->posts[ $symbol ]->$property : null;
			$value    = str_replace( $match, $expanded, $value );

		}

		return ( is_numeric( $value ) && false === strpos( $value, '.' ) ) ? (int) $value : $value;

	}

}

function wpem_the_reasons_fields() {

	$fields = [
		'have_a_design' => wpem_localize( "I already have a different theme or template I plan on using" ),
		'page_builder'  => wpem_localize( 'I plan to use a page builder (e.g. Elementor, Divi, Beaver Builder)' ),
		'do_not_like'   => wpem_localize( "I don't like any of these template options" ),
		'not_ready'     => wpem_localize( "I'm not ready to pick a design right now" ),
	];

	uksort( $fields, function () {

		return rand() > getrandmax() / 2;

	} );

	$rand = mt_rand();

	foreach ( $fields as $field => $label ) {

		printf(
			'<div><label for="reasons-%3$s-%1$s"><input type="checkbox" name="reasons" id="reasons-%3$s-%1$s" value="%1$s"><span>%2$s</span></label></div>',
			esc_attr( $field ),
			esc_html( $label ),
			esc_attr( $rand )
		);

	}

	?>
	<div style="display: flex;">
		<?php if ( is_rtl() ) : ?>
			<label for="reasons-other"><input type="checkbox" name="reasons" id="reasons-other" value="other"><?php echo esc_html( wpem_localize( 'Other reason:' ) ); ?></label>
			<input type="input" name="reason_other" id="reason_other" disabled="disabled" style="flex-grow: 1;">
		<?php else : ?>
			<label for="reasons-other"><input type="checkbox" name="reasons" id="reasons-other" value="other"><?php echo esc_html( wpem_localize( 'Other reason:' ) ); ?></label>
			<input type="input" name="reason_other" id="reason_other" disabled="disabled" style="flex-grow: 1;">
		<?php endif; ?>
	</div>
	<?php

}

