								<option value="store-featured" data-url="<?php echo esc_url_raw( wpem_wpnux_api_url( 'templates', [ 'lang' => get_locale(), 'pl' => 1, 'where[store]' => 1, 'where[featured]' => 1 ] ) ); ?>"><?php echo esc_html( wpem_localize( 'Featured' ) ); ?></option>
