						<h2><?php echo esc_html( wpem_localize( 'What are you planning to do with your website?' ) ); ?></h2>
						<p><?php echo esc_html( wpem_localize( "Check all that apply and we'll set up your website accordingly." ) ); ?></p>
						<form>
							<div class="fields feature-fields"></div>
							<h3><?php echo esc_html( wpem_localize( 'Are you building this website for yourself or someone else?' ) ); ?></h3>
							<p><?php echo esc_html( wpem_localize( 'Choose the option that best describes your situation.' ) ); ?></p>
							<div class="fields persona-fields">
								<?php wpem_the_persona_fields(); ?>
