			<div class="step-4">
				<h2><?php echo esc_html( wpem_localize( 'Installing your template' ) ); ?></h2>
				<p><?php echo esc_html( wpem_localize( "Don't worry, this won't take long." ) ); ?></p>
				<div class="icon"><svg aria-hidden="true" focusable="false" class="spinner" role="img" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100" preserveAspectRatio="xMidYMid"><circle cx="50" cy="50" r="32" stroke-width="8" stroke="#2b2b2b" stroke-dasharray="50.26548245743669 50.26548245743669" fill="none" stroke-linecap="round" transform="rotate(5.68656 50 50)"><animateTransform attributeName="transform" type="rotate" repeatCount="indefinite" dur="1s" keyTimes="0;1" values="0 50 50;360 50 50"></animateTransform></circle></svg></div>
			</div>
		</div>
		<?php wp_print_scripts( 'jquery' ); ?>
		<script type="text/javascript">
		jQuery(document).ready( function($) {
			/**
			 * Populate templates into the view via type (website/store).
			 */
			function wpem_populate_verticals(type) {
				if ( type == 'store' ) {
					var url = "<?php echo esc_url_raw( wpem_wpnux_api_url( 'verticals', [ 'lang' => get_locale(), 'pl' => 1, 'store' => 1 ] ) ); ?>";
					var $list = $('main nav.sidebar ul.store');
					var $optgroup = $('main nav.dropdown select optgroup.store');
				} else if ( type == 'website' ) {
					var url = "<?php echo esc_url_raw( wpem_wpnux_api_url( 'verticals', [ 'lang' => get_locale(), 'pl' => 1 ] ) ); ?>";
					var $list = $('main nav.sidebar ul.website');
					var $optgroup = $('main nav.dropdown select optgroup.website');
				}
				$.getJSON( url, function( verticals ) {
					var items = [];
					var options = [];
					$.each( verticals, function( index, vertical ) {
						items.push('<li><a href="javascript:void(0)" data-slug="' + type + '-' + vertical.slug + '" data-vertical="' + vertical.slug + '" data-url="' + vertical.templates_url + '">' + vertical.label + '</a></li>');
						options.push('<option value="' + type + '-' + vertical.slug + '" data-url="' + vertical.templates_url + '">' + vertical.label + '</option>');
					});
					$list.append( items.join("") );
					$optgroup.append( options.join("") );
				});
			}

			/**
			 * Load list of website verticals (default).
			 */
			wpem_populate_verticals('website');

			/**
			 * Load list of store verticals.
			 */
			wpem_populate_verticals('store');

			const sidebar_offset_top = $('nav.sidebar').offset().top;

			/**
			 * Populate templates into the view via API URL.
			 */
			function wpem_populate_templates(url) {
				$('.templates').css('min-height', $('.templates ul').height());

				$.getJSON( url, function( templates ) {
					var items = [];
					var preview_label = "<?php echo esc_html( wpem_localize( 'Preview' ) ); ?>";
					$.each( templates, function( index, template ) {
						items.push('<li><a href="javascript:void(0)" data-api-url="' + template.self_url + '" data-preview-url="' + template.preview_url + '"><img src="' + template.screenshot_url.replace( ".jpg", "@2x.jpg" ) + '"><div class="overlay"><span class="icon"><svg aria-hidden="true" focusable="false" class="eye" role="img" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 576 512"><path fill="currentColor" d="M288 144a110.94 110.94 0 0 0-31.24 5 55.4 55.4 0 0 1 7.24 27 56 56 0 0 1-56 56 55.4 55.4 0 0 1-27-7.24A111.71 111.71 0 1 0 288 144zm284.52 97.4C518.29 135.59 410.93 64 288 64S57.68 135.64 3.48 241.41a32.35 32.35 0 0 0 0 29.19C57.71 376.41 165.07 448 288 448s230.32-71.64 284.52-177.41a32.35 32.35 0 0 0 0-29.19zM288 400c-98.65 0-189.09-55-237.93-144C98.91 167 189.34 112 288 112s189.09 55 237.93 144C477.1 345 386.66 400 288 400z"></path></svg></span><span class="text">' + preview_label + '</span></div></a><h3>' + template.label + '</h3></li>');
					});

					$('.templates ul').hide().html( items.join("") ).delay(250);

					var fadeInTemplates = function () {
						$('.templates ul').css('min-height', '').fadeIn(250, function() {
							$('.templates').css('min-height', '');
						});
					};

					if ($(window).scrollTop() > sidebar_offset_top) {
						$('html, body').scrollTop(sidebar_offset_top);
						fadeInTemplates();
					} else {
						fadeInTemplates();
					}
				});
			}

			/**
			 * First load of all templates (Featured)
			 */
			wpem_populate_templates("<?php echo esc_url_raw( wpem_wpnux_api_url( 'templates', [ 'lang' => get_locale(), 'pl' => 1, 'where[featured]' => 1 ] ) ); ?>");

			/**
			 * Change the vertical list type (website/store)
			 */
			$( "main nav .types" ).on( 'click', 'span:not(.active)', function(e) {
				$("main nav .types span").toggleClass("active");
				$("main nav ul").toggleClass("active");
				$("main nav ul.active a:nth(1)").click(); // Featured
			});

			/**
			 * Click a vertical from the sidebar list
			 */
			$('main nav.sidebar ul').on('click', 'a', function(e) {
				e.preventDefault();
				$('main nav.sidebar li').removeClass('active');
				$(this).closest('li').addClass('active');
				$('main nav.dropdown select').val( $(this).data('slug') ).prop('selected', true); // Make the dropdown match.
				var url = $(this).data('url');
				$('.templates ul').fadeOut( 250, function() {
					wpem_populate_templates(url);
				});
				$('#import-vertical').val( $(this).data('vertical') );
			});

			/**
			 * Select a vertical from the mobile dropdown
			 */
			$('main nav.dropdown select').on('change', function() {
				var url = $(this).data('url');
				$('main nav.sidebar li a[data-slug="' + $(this).val() + '"]').click(); // Trigger a list click to load templates.
			});

			/**
			 * Click a template and launch preview frame
			 */
			$('.templates').on('click', 'a:not(.loading)', function() {
				if ( $('.templates a').hasClass('loading') ) {
					return;
				}
				var $link = $(this);
				$link
					.addClass('loading')
					.find('.overlay .icon')
						.append('<svg aria-hidden="true" focusable="false" class="spinner" role="img" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100" preserveAspectRatio="xMidYMid"><circle cx="50" cy="50" r="32" stroke-width="8" stroke="#2b2b2b" stroke-dasharray="50.26548245743669 50.26548245743669" fill="none" stroke-linecap="round" transform="rotate(5.68656 50 50)"><animateTransform attributeName="transform" type="rotate" repeatCount="indefinite" dur="1s" keyTimes="0;1" values="0 50 50;360 50 50"></animateTransform></circle></svg>');
				$('.step-2')
					.css('top', $(window).height() + $(window).scrollTop() + 'px')
					.find('.controls a.button')
						.attr('data-api-url', $link.data('api-url'));
				$('#preview-frame')
					.load( function() {
						$("#preview-frame-wrapper").css('height', 'calc(100% - ' + $('.controls').outerHeight(true) + 'px');
						$('.step-2')
							.show()
							.animate({ top: $(window).scrollTop() + 'px' }, 400, function() {
								$('body').addClass('on-step-2').removeClass('on-step-1 on-step-3');
								$link
									.removeClass('loading')
									.find('.overlay .icon .spinner')
										.remove();
							});
				});
				document.getElementById('preview-frame').contentWindow.location.replace( $link.data('preview-url') );
			});

			/**
			 * Override scroll behavior during preview
			 */
			$(document).on("scroll", function(e) {
				if ( $('body').hasClass('on-step-2') ) {
					$(document).scrollTop( $(".step-2").offset().top );
				}
			});

			/**
			 * Allow the ESC key to exit a preview
			 */
			$(document).on("keyup", function(e) {
				if ( e.keyCode === 27 ) {
					$(".step-2 .controls a.close").click();
				}
			});

			/**
			 * Toggle the preview device size
			 */
			$('.devices').on('click', 'a:not(.active)', function(e) {
				$('.devices a').toggleClass('active');
				$('#preview-frame-wrapper')
					.removeClass()
					.addClass( $(this).data('size') );
			});

			/**
			 * Close the preview frame and go back to view templates
			 */
			$( ".step-2 .controls" ).on( 'click', 'a.close', function() {
				$(".step-2")
					.finish();
				$("body")
					.addClass('on-step-1')
					.removeClass('on-step-2 on-step-3');
				$(".step-2")
					.animate({ top: $(window).height() + $(window).scrollTop() + 'px' }, 400, function () {
						$(this).removeAttr('style');
						$('#preview-frame-wrapper')
							.removeClass()
							.addClass('desktop');
						$('.devices a').removeClass('active');
						$('.devices a.desktop').addClass('active');
					});
			});

			/**
			 * Select the template during preview and go to feature selection
			 */
			$( ".step-2 .controls" ).on( 'click', 'a.button', function() {
				$(".step-2").finish();
				$("body").addClass('on-step-3').removeClass('on-step-1 on-step-2');
				$.getJSON( $(this).attr('data-api-url'), function( template ) {
					$('.step-3 .screenshot img')
						.attr( 'src', template.screenshot_url.replace( ".jpg", "@2x.jpg" ) );
					var items = [];
					$.each( template.features, function( index, feature ) {
						is_checked = feature.checked ? 'checked="checked"' : '';
						is_disabled = feature.disabled ? 'disabled="disabled"' : '';
						items.push('<div><label for="feature-' + feature.slug + '"><input type="checkbox" name="features" id="feature-' + feature.slug + '" value="' + feature.slug + '" ' + is_checked + ' ' + is_disabled + '><span>' + feature.label + '</span></label></div>');
					});
					$('.features .feature-fields')
						.html( items.join("") )
						.append('<input type="hidden" name="template" value="' + template.slug + '">')
						.append('<input type="hidden" name="preview_url" value="' + template.preview_url + '">');
					$(".step-2")
						.animate({ top: $(window).height() + $(window).scrollTop() + 'px' }, 400, function() {
							$(this).removeAttr('style');
							$('#preview-frame-wrapper')
								.removeClass()
								.addClass('desktop');
							$('.devices a').removeClass('active');
							$('.devices a.desktop').addClass('active');
						});
				});
			});

			/**
			 * Launch the exit modal
			 */
			 $( ".step-1 .exit, .step-1 .skip" ).on( 'click', 'a', function() {
				$('.exit-modal').css( 'visibility', 'visible' );
				$('.exit-modal-close-icon').show();
				$('.exit-modal-confirm').show();
				$('.exit-modal-poll').hide();
				$('body').css( 'overflow', 'hidden' );
			});

			/**
			 * Cancel the exit modal
			 */
			 $( ".exit-modal" ).on( 'click', '.exit-modal-close', function() {
				$('.exit-modal-close-icon').hide();
				$('.exit-modal-confirm').hide();
				$('.exit-modal-poll').hide();
				$('.exit-modal').css( 'visibility', 'hidden' );
				$('body').css( 'overflow', 'auto' );
			});

			/**
			 * Launch the exit poll
			 */
			 $( ".exit-modal" ).on( 'click', '#exit-modal-confirm-yes', function() {
				$('.exit-modal-confirm').hide();
				$('.exit-modal-poll').show();
			});

			/**
			 * Toggle the exit poll "Other" field
			 */
			$( '.exit-modal-poll input[name="reasons"]' ).on( 'change', function(e) {
				if ( 'reasons-other' === $(this).attr( 'id' ) ) {
					if ( 'checked' === $(this).attr( 'checked' ) ) {
						$( '#reason_other' )
							.removeAttr( 'disabled' )
							.focus();
					} else {
						$( '#reason_other' )
							.attr( 'disabled', 'disabled' )
							.val( '' );
					}
				} else if ( ! $( '#reason_other' ).val() ) {
					$( '#reason_other' )
						.attr( 'disabled', 'disabled' )
						.val( '' );
					$( '#reasons-other' )
						.removeAttr( 'checked' );
				}
			});

			/**
			 * Finish the exit poll
			 */
			$( ".exit-modal-poll .button" ).one( 'click', function(e) {
				e.preventDefault();
				$('body')
					.addClass('on-step-4')
					.removeClass('on-step-1');
				$('.step-4').addClass('exiting');
				var persona = null;
				var reasons = [];
				var reason_other = null;
				if ( 'exit-poll-submit' === $(this).attr('id') ) {
					persona = $('.exit-modal-poll input[name="persona"]:checked').val();
					$('.exit-modal-poll input[name="reasons"]:checked').each( function() {
						reasons.push( $(this).val() );
					});
					reason_other = $('.exit-modal-poll input#reason_other').val();
				}
				var data = {
					'action': 'wpem_nux_optout',
					'nonce': "<?php echo wp_create_nonce( 'wpem_nux_optout' ); // phpcs:ignore WordPress.Security.EscapeOutput ?>",
					'persona': persona,
					'reasons': reasons.join(','),
					'reason_other': reason_other
				};
				$.post("<?php echo esc_url( admin_url( 'admin-ajax.php' ) ); ?>", data, function(response) {
					if ( typeof response.success != 'undefined' && response.success ) {
						window.location.href = "<?php echo esc_url_raw( admin_url() ); ?>";
					} else {
						console.log(response);
					}
				});
			});

			/**
			 * Exit feature selection and go back to view templates
			 */
			$( ".step-3 .exit" ).on( 'click', 'a', function() {
				$('.step-3 .screenshot img')
					.attr('src', '');
				$('.step-3 .features .feature-fields')
					.empty();
				$('body')
					.addClass('on-step-1')
					.removeClass('on-step-2 on-step-3');
			});

			/**
			 * Install the template
			 */
			$( ".step-3 form a.button" ).one( 'click', function(e) {
				e.preventDefault();
				$('body')
					.addClass('on-step-4')
					.removeClass('on-step-3');
				var features = [];
				$('.step-3 form input[name="features"]:checked').each( function() {
					features.push( $(this).val() );
				});
				var data = {
					'action': 'wpem_nux_import',
					'nonce': "<?php echo wp_create_nonce( 'wpem_nux_import' ); // phpcs:ignore WordPress.Security.EscapeOutput ?>",
					'features': features.join(','),
					'persona': $('.step-3 form input[name="persona"]:checked').val(),
					'preview_url': $('.step-3 form input[name="preview_url"]').val(),
					'template': $('.step-3 form input[name="template"]').val()
				};
				$.post("<?php echo esc_url( admin_url( 'admin-ajax.php' ) ); ?>", data, function(response) {
					if ( typeof response.success != 'undefined' && response.success ) {
						window.location.href = "<?php echo esc_url_raw( home_url( '/' ) ); ?>";
					} else {
						console.log(response);
					}
				});
				e.stopImmediatePropagation();
				return false;
			});

		});
		</script>
	</body>
</html>
<?php

exit;

}
